mapper.select:
	select 

	from account
	where status="0" 

		and manager_up_id =#{managerUpId,jdbcType=INTEGER}

  order by id ASC  limit #{startNum,jdbcType=INTEGER},#{pageNumber,jdbcType=INTEGER}

 	select a.id, a.Uuid, a.openid, a.nickName, a.headIcon, a.roomCard, a.unionid, a.province, a.city, a.sex,a.redCard,
   a.prizecount, a.actualCard, a.totalCard, a.createTime, a.lastLoginTime, a.status, a.isGame,a.managerId,b.inviteCode as manager_up_id from account a left join manager b on a.manager_up_id = b.id
     	where a.status!="2" 

       and a.manager_up_id = #{managerUpId,jdbcType=INTEGER} 


       and b.pid = #{pid,jdbcType=INTEGER} 


	and nickName like "%"#{nickname}"%"


	and a.createTime > #{startTime,jdbcType=TIMESTAMP}


	and a.createTime < #{endTime,jdbcType=TIMESTAMP}


   order by a.id DESC  limit #{startNum,jdbcType=INTEGER},#{pageNumber,jdbcType=INTEGER}

 	select 
 	IFNULL(count(*),0)
 	from account
 	where status!="2" 

       and manager_up_id = #{managerUpId,jdbcType=INTEGER} 


	and createTime > #{startTime,jdbcType=TIMESTAMP}



	and nickName like "%"#{nickname}"%"



	and createTime < #{endTime,jdbcType=TIMESTAMP}

mapper.update:
	update account
	set  roomCard = roomCard+#{roomcard,jdbcType=INTEGER}
  where managerId =#{managerid,jdbcType=INTEGER}

mapper.select:
	select 

  from account
  where managerId = #{managerId,jdbcType=INTEGER} and status = "0";
